class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:

        d = {}
        for i in range(30):
            d[i] = -1

        cnt = 0
        idx = -1
        for i in range(len(nums)):

            c = list(d.items())
            c.sort(reverse = True, key = lambda x: x[1])
            curr = nums[i]
            if curr == k:
                idx = i

            for j in range(len(c)):
                curr &= ~(1 << c[j][0])
                if j < len(c) - 1 and c[j + 1][1] == c[j][1]:
                    continue

                if idx != -1:
                    cnt += idx - c[j][1]
                if curr == k:
                    idx = c[j][1]
                else:
                    idx = -1



            if idx != -1:
                cnt += idx + 1

            idx = -1

            it = 0
            for j in range(30):
                if nums[i] % 2 == 0:
                    d[it] = i
                it += 1
                nums[i] >>= 1



        return cnt
