class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        long long cnt = 0;
        int n = a.size();

        unordered_map<int, int> mp1;

        for (int num : a) {
            unordered_map<int, int> mp2;

            if (num == k) {
                cnt++;
            }
            mp2[num] = mp2[num] + 1;

            for (auto &x : mp1) {
                int newAnd = x.first & num;
                if (newAnd == k) {
                    cnt += x.second;
                }
                mp2[newAnd] = mp2[newAnd] + x.second;
            }

            mp1 = mp2;
        }
        return cnt;
    }
};
