class Solution {
public:
    typedef long long ll;
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size(),cnt = 0;
        map<ll,ll>mp;
        for(int i = 0; i < n; i++) 
        {
            map<ll,ll>mp1; 
            if (nums[i] == k)
            {
                cnt++;
            }
            mp1[nums[i]]++;
            // cout<<cnt<<endl;
            for(auto it: mp) 
            {
                ll a = it.first & nums[i];
                if(a == k) 
                {
                    // cout<<it.second<<endl;
                    cnt += it.second;
                }
                mp1[a] += it.second;
            }
            mp = mp1;
        }
        return cnt;
    }
};
