class Solution {
public:
    #define ll long long int 

    long long countSubarrays(vector<int>& arr, int k) {
        int n=arr.size();
        ll ans=0;
        unordered_map<ll,ll>mp;
        for(int i=0;i<n;i++){
            unordered_map<ll,ll>mp2;
            if(arr[i]==k){
                ans++;
            }
            mp2[arr[i]]++;
            for(auto &it:mp){
                ll zz = it.first;
                ll xx = zz & arr[i];
                // cout<<xx<<" "<<zz<<endl;
                if(xx==k){
                    ans+=it.second;
                }
                mp2[xx]+=it.second;
            }

            mp=mp2;
        }
        return ans;

    }
};
