class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        long c = 0;
        int z=0;
        Map<Long, Long> ans = new HashMap<>();

        for (int i = 0; i < n; ++i) {
            Map<Long, Long> tempans = new HashMap<>();

            if (nums[i] == k) {
                c++;
                z--;
            }
            tempans.put((long)nums[i], 1L);
            z++;

            for (Map.Entry<Long, Long> entry : ans.entrySet()) {
                long val = entry.getKey();
                long freq = entry.getValue();
                long newand = val & nums[i];
                if (newand == k) {
                    c += freq;
                }
                tempans.put(newand, tempans.getOrDefault(newand, 0L) + freq);
                z++;
            }

            ans = tempans;
        }

        return c;
    }
}
