class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int sz = nums.size();
        vector<int> zeros(31, -1);

        for (int i = 0; i < sz; i++) {
            for (int j = 0; j < 31; j++) {
                if (!(nums[i] & (1 << j))) {
                    zeros[j] = i;
                }
            }

            int left = -1;
            int right = numeric_limits<int>::max();

            for (int j = 0; j < 31; j++) {
                if (k & (1 << j)) {
                    left = max(left, zeros[j]);
                } else {
                    right = min(right, zeros[j]);
                }
            }
            ans += max(0, right - left);
        }

        return ans;
    }
};
