class Solution {
public:
    long long countSubarrays(vector<int>& elements, int target) {
        int size = elements.size();
        long long totalCount = 0;

        unordered_map<long long, long long> previousResults;

        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> currentResults;

            if (elements[index] == target) {
                ++totalCount;
            }
            currentResults[elements[index]] = 1;

            for (auto& [value, frequency] : previousResults) {
                long long newAndResult = value & elements[index];
                if (newAndResult == target) {
                    totalCount += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = currentResults;
        }

        return totalCount;
    }
};
