class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
    unordered_map<long long, long long> andMap;
    long long result = 0;

    for (long long i = 0; i < n; ++i) {
        unordered_map<long long, long long> newAndMap;
        for (auto &entry : andMap) {
            long long newAnd = entry.first & nums[i];
            newAndMap[newAnd] += entry.second;
        }
        newAndMap[nums[i]]++;
        andMap = newAndMap;

        if (andMap.find(k) != andMap.end()) {
            result += andMap[k];
        }
    }

    return result;
    }
};
