class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long ans=0;unordered_map<long long,long long>mp;for(int i=0;i<n;i++){
            unordered_map<long long,long long>m1;
            if(nums[i]==k){ans++;}m1[nums[i]]=1;
            for(auto it:mp){
                long long x=it.first&nums[i];if(x==k){ans+=it.second;}
                m1[x]+=it.second;
            }
            mp=m1;
        }return ans;


    }
};
