#define umpii unordered_map<int, int>
#define umpci unordered_map<char, int>
#define ustii unordered_set<int>
#define ll long long
#define pii pair<int, int>
#define vi vector<int>
#define vpii vector<pii>
#define vll vector<long long>
#define nl cout << endl
#define prt(v) for (auto &i : v) cout << i << " "; cout << nl;
#define take(v) for (auto &i : v) cin >> i;

class Solution {
public:
    ll countSubarrays(vi& numbers, int key) {
        int length = numbers.size();
        ll totalCount = 0;

        umpii previousResults;
        int position = 0;

        while (position < length) {
            umpii currentResults;

            if (numbers[position] == key) {
                ++totalCount;
            }
            currentResults[numbers[position]] = 1;

            for (auto& [prevValue, frequency] : previousResults) {
                ll newAndValue = prevValue & numbers[position];
                if (newAndValue == key) {
                    totalCount += frequency;
                }
                currentResults[newAndValue] += frequency;
            }

            previousResults = currentResults;
            ++position;
        }

        return totalCount;
    }
};
