class Solution {
public:
    long long countSubarrays(vector<int>& nums, int p) {
        int n = nums.size();
        vector<int> ans(n);
        vector<pair<int, int>> ors; // 按位或的值 + 对应子数组的右端点的最小值
        long long cnt = 0;
        for (int i = n - 1; i >= 0; --i) {
            ors.emplace_back(-1, 1);
            ors[0].first &= nums[i];
            int k = 0;
            for (int j = 1; j < ors.size(); ++j) {
                ors[j].first &= nums[i];
                if (ors[k].first == ors[j].first)
                    ors[k].second += ors[j].second; // 合并相同值，下标取最小的
                else ors[++k] = ors[j];
            }
            ors.resize(k + 1);
            for (int j = 0; j < ors.size(); ++j) {
                if(ors[j].first == p){
                    cnt += ors[j].second;
                }
            }

        }
        return cnt;
    }
};
