class Solution(object):
    def countSubarrays(self, nums, k):
        n = len(nums)
        res = 0
        mp = dict()

        for i in range(n):
            mp2 = dict()
            if nums[i] == k:
                res += 1
            mp2[nums[i]] = 1

            for x, y in mp.items(): 
                p = x & nums[i]
                if p == k:
                    res += y
                if p in mp2:
                    mp2[p] += y
                else:
                    mp2[p] = y

            mp = mp2.copy()

        return res
