class Solution {
public:
  int rangeand(vector<int> &bits,int len){
    int ans=0;
    for(int b=0;b<31;b++){
        if(bits[b]==len) ans+=(1<<b);
    }
    return ans;
  }
    long long atleastk(vector<int> &nums,int k){
        vector<int> bits(32,0);
        long long ans=0;
        int i=0;
        for(int j=0;j<nums.size();j++){
            for(int b=0;b<31;b++){
                if((1<<b)&nums[j]) bits[b]++;
            }
            while(i<=j&&rangeand(bits,j-i+1)<k){
                for(int b=0;b<31;b++){
                if((1<<b)&nums[i]) bits[b]--;
            }
                i++;
            }
            ans=ans+(j-i+1);
        }
        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        return atleastk(nums,k)-atleastk(nums,k+1);
    }
};
