#define ll long long


class Solution {
    private:
    void give(map<ll,ll>& pr,int idx, map<ll, ll>& cr, ll & tot,vector<int>& nums, int k) {
    for (auto& [val, f] : pr) {
        ll nar = nums[idx] & val;
        if (nar == k) {
            tot += f;
        }
        cr[nar] += f;
    }
}
public:
    long long countSubarrays(vector<int>& nums, int k) {
        ll n = nums.size(), ans = 0;
       map<ll, ll> pr;
        for (int i = 0; i < n; ++i) {
            map<ll, ll> cr;
            if (nums[i] == k) {
                ans += 1;
            }
            cr[nums[i]] = 1;
            give(pr,i, cr, ans, nums,k);

            pr = cr;
        }
        return ans;
    }
};
