class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) 
    {
        int p=nums.size();
        long long cnt=0;
        unordered_map<long long,long long> prevres;
        vector<int> done(p,0);
        for(int i=0;i<p;i++)
        {
            unordered_map<long long,long long> curr_res;
            if(nums[i]==k){
                ++cnt;
            }
            curr_res[nums[i]]=1;

            for(auto& [v,fr]: prevres)
            {
                long long new_res=v&nums[i];
                if(new_res==k)
                {
                    cnt+=fr;
                }
                curr_res[new_res]+=fr;
            }

            prevres=curr_res;
        }
        return cnt;
    }
};
