class Solution {
public:
    long long countSubarrays(vector<int>&temp, int tar) {
        int lenn = temp.size();

        long long finalAns = 0;

        unordered_map<long long, long long>mp1;

        for(int ind=0; ind<lenn; ind++){
            unordered_map<long long, long long>mp2;

            mp2[temp[ind]]=1;

            if(temp[ind]==tar) finalAns++;

            for(auto &it : mp1){
                long long x = it.first;
                long long y = it.second;
                long long newValue = x & temp[ind];
                if(newValue == tar){
                    finalAns+=y;
                }
                mp2[newValue] += y;
            }

            mp1 = mp2;
        }

        return finalAns;
    }
};
