class Solution {
public:

    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int n = nums.size();
        unordered_map<long long ,long long > mp;
        for(int i = 0; i<n; i++){
            unordered_map<long long ,long long> mp2;
            int num = nums[i];
            if(num == k){
                ans++;
            }
            mp2[num] = 1;
            for(auto it : mp){
                long long x = num & it.first;
                if(x == k){
                    ans += it.second;
                }
                mp2[x] += it.second;
            }
            mp = mp2;
        }
        return ans;
    }
};
