class Solution {
public:
    long long countSubarrays(vector<int>& v, int x) {
        long long ans=0,n=v.size();
        unordered_map<int,vector<int>> m;
        for(int b=0;b<=30;b++){
            int k=(1<<b);
            for(int i=0;i<n;i++){
                if(v[i]&k)
                continue;
                else m[b].push_back(i);
            }
        }
        for(int i=0;i<n;i++){
            int l=i,h=n-1;
            for(int b=0;b<=30;b++){
                int k=(1<<b),ind=n;
                auto it=lower_bound(m[b].begin(),m[b].end(),i);
                if(it!=m[b].end()){
                    ind=*it;
                }
                if(k&x){
                    h=min(h,ind-1);
                }
                else{
                    l=max(l,ind);
                }
            }
            if(l<=h){
                ans+=(h-l+1);
            }
        }
        return ans;
    }
};
