class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int size = nums.size();
        long long total = 0;

        unordered_map<long long, long long> prevResults;

        for(int index = 0; index < size; ++index) {
            unordered_map<long long, long long> currResults;

            if(nums[index] == k) {
                ++total;
            }

            currResults[nums[index]] = 1;

            for(auto& [value, frequency] : prevResults) {
                long long andResult = value & nums[index];

                if(andResult == k) {
                    total += frequency;
                }

                currResults[andResult] += frequency;
            }

            prevResults = currResults;

        }

        return total;
    }
};
