class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) 
    {
        map<int, long long> f;
        long long ans = 0;
        int n = nums.size();
        for (int i = n - 1; i >= 0; i--)
        {
            map<int, long long> nf;
            for (auto j : f)
            {
                int temp = j.first & nums[i];
                nf[temp] += j.second;
            }
            nf[nums[i]]++;
            f = nf;
            ans += f[k];
        }    
        return ans;
    }
};
