from typing import List
from collections import defaultdict
class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n=len(nums)
        total=0
        res=defaultdict(int)

        for i in range(n):
            res1=defaultdict(int)
            if nums[i]==k:
                total+=1
            res1[nums[i]]=1

            for value,frequency in res.items():
                newres=value&nums[i]
                if newres==k:
                    total+=frequency
                res1[newres]+=frequency
            res=res1
        return total
