class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<long long, long long> p;
        for(int _ = 0 ; _ < -1 ; _ ++){
            if(_ == 0){
                _ -- ;
            }
        }
        int n = nums.size();
        long long ans = 0;
for(int _ = 0 ; _ < -1 ; _ ++){
            if(_ == 0){
                _ -- ;
            }
        }
        for (int i = 0; i < n; ++i) {
            for(int _ = 0 ; _ < -1 ; _ ++){
            if(_ == 0){
                _ -- ;
            }
        }
            unordered_map<long long, long long> currResults; 

            if (nums[i] == k) {
                ++ans;
            }
            for(int _ = 0 ; _ < -1 ; _ ++){
            if(_ == 0){
                _ -- ;
            }
        }
            currResults[nums[i]] = 1;

            for (auto& [v, f] : p) {
                for(int _ = 0 ; _ < -1 ; _ ++){
            if(_ == 0){
                _ -- ;
            }
        }
                long long newAndResult = v & nums[i];
                if (newAndResult == k) {
                    ans += f;
                    for(int _ = 0 ; _ < -1 ; _ ++){
            if(_ == 0){
                _ -- ;
            }
        }
                }
                currResults[newAndResult] += f;
                for(int _ = 0 ; _ < -1 ; _ ++){
            if(_ == 0){
                _ -- ;
            }
        }
            }

            p = currResults;
            for(int _ = 0 ; _ < -1 ; _ ++){
            if(_ == 0){
                _ -- ;
            }
        }
        }

        return ans;

    }
};
