class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size(), i = 0;
        ll ans = 0;
        ulmap prev;

        FOR(i,0,n){
            ulmap curr;
            if(arr[i] == k)
                ans++;
            curr[arr[i]] = 1; //up to 1 

            for(auto it: prev) { //check earlier added values
                auto val = it.first, cnt = it.second;
                //cout << val << "," << cnt << endl;
                // auto cnt = it.second;
                ll newVal = val & arr[i]; //updating newVal to previously if existed
                if(newVal == k) ans += cnt;
                curr[newVal] += cnt;
            }
            prev = curr; //update dp
        }
        return ans;
    }
};
