#define N 100002

long long tree[4 * N];

void build(vector<int> &arr, int node, int start, int end) {
    if (start == end) {
        tree[node] = arr[start];
        return;
    }
    int mid = (start + end) >> 1;
    build(arr, 2 * node, start, mid);
    build(arr, 2 * node + 1, mid + 1, end);
    tree[node] = tree[2 * node] & tree[2 * node + 1];
}

long long query(int node, int start, int end, int l, int r) {
    if (start > end || start > r || end < l) {
        return -1;
    }

    if (start >= l && end <= r) {
        return tree[node];
    }

    int mid = (start + end) >> 1;
    long long q1 = query(2 * node, start, mid, l, r);
    long long q2 = query(2 * node + 1, mid + 1, end, l, r);
    return q1 & q2;
}

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long c1 = 0, c2 = 0;
        int n = nums.size();
        build(nums, 1, 0, n - 1);
        for (int i = 0; i < n; i++) {
            int l = i, h = n - 1;
            long long index = INT_MIN;
            while (l <= h) {
                int mid = (l + h) / 2;
                if (query(1, 0, n - 1, i, mid) >= k) {
                    index = max(index, (long long)mid);
                    l = mid + 1;
                } else {
                    h = mid - 1;
                }
            }
            if (index != INT_MIN) c1 += index - i + 1;
        }

        for (int i = 0; i < n; i++) {
            int l = i, h = n - 1;
            long long index = INT_MIN;
            while (l <= h) {
                int mid = (l + h) / 2;
                if (query(1, 0, n - 1, i, mid) >= (k + 1)) {
                    index = max(index, (long long)mid);
                    l = mid + 1;
                } else {
                    h = mid - 1;
                }
            }
            if (index != INT_MIN) c2 += index - i + 1;
        }

        return c1 - c2;
    }
};
