class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long count = 0;
        unordered_map<int, int> andMap;

        for (int i = 0; i < n; ++i) {
            unordered_map<int, int> newMap;

            if (nums[i] == k) {
                ++count;
            }

            newMap[nums[i]]++;

            for (auto& [key, val] : andMap) {
                int newAnd = key & nums[i];
                if (newAnd == k) {
                    count += val;
                }
                newMap[newAnd] += val;
            }

            andMap = newMap;
        }

        return count;
    }
};
