class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
          long long ans=0;
          int n=nums.size();
          map<int,int>mp1;
         for(int i=0;i<n;i++) {
            map<int,int>mp2; 
            if(nums[i]==k)
                ans++;
            mp2[nums[i]] = 1;
            for(auto it:mp1) {
                int temp=nums[i]&it.first;
                if (temp==k) {
                    ans=ans+it.second;
                }
                mp2[temp] =mp2[temp]+it.second;
            }    
            mp1=mp2;
        }  
        return ans;
    }
};
