class Solution {
public:
    map<long long ,long long>mpp;
    long long countSubarrays(vector<int>& v, int k) {
        long long ans=0;

        int n=v.size();
        int i=0;
        while(i<n){
            map<long long ,long long>mp;
            if(v[i]!=k){
                mp[v[i]]=1;
                for(auto &it:mpp){
                    long long a=it.first;
                    long long b=it.second;
                    long long end=a&v[i];
                    if(end==k)ans+=b;
                    mp[end]+=b;
                }
                mpp=mp;
            }
            else{
                ans+=1;
                 mp[v[i]]=true;
                for(auto &it:mpp){
                    long long a=it.first;
                    long long b=it.second;
                    long long end=a&v[i];
                    if(end==k)ans+=b;
                    mp[end]+=b;
                }
                mpp=mp;
                mp.clear();
            }
            i+=1;
        }
        return ans;

    }
};
