class Solution {
public:
    bool check(int k,int x){
        while(k){
            if(k&1){
                if(x&1){
                    k>>=1;
                    x>>=1;
                }
                else{
                    return false;
                }
            }
            else{
                k>>=1;
                x>>=1;
            }
            // if( !(k&1) || (k&1 && x&1))
        }
        return true;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        int n=nums.size();
        long long res=0;
        unordered_map<int,int> mp1,mp2;
        for(int i=1;i<=n;i++){
            int cur=nums[i-1];
            if(!check(k,cur)){
                mp1.clear();
                continue;
            }
            mp2[cur]++;
            for(auto& p:mp1){
                int j=p.first,count=p.second;
                int tmp=j&cur;
                if(check(k,tmp)){
                    mp2[tmp]+=count;
                }
            }
            res+=mp2[k];
            mp1=mp2;
            mp2.clear();
        }
        return res;
    }
};
