class Solution {
public:
    long andSum(vector<vector<int>>& x, int y) {
        long ans = 0, z = 1;
        for(int i = 0 ; i < x[0].size() ; i++) {
            int d = y % 2;
            y /= 2;
            if(d == 0) x[0][i]++;
            else x[1][i]++;
            if(x[1][i] > 0 && x[0][i] == 0) ans += z;
            z *= 2;
        }
        return ans;
    }

    long removesum(vector<vector<int>>& x, int y) {
        long ans = 0, z = 1;
        for(int i = 0 ; i < x[0].size() ; i++) {
            int d = y % 2;
            y /= 2;
            if(d == 0) x[0][i]--;
            else x[1][i]--;
            if(x[1][i] > 0 && x[0][i] == 0) ans += z;
            z *= 2;
        }
        return ans;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans = 0;
        int j = 0;
        int sum = 0;
        vector<vector<int>> x(2, vector<int> (32, 0));
        for(int i = 0; i < nums.size() ; i++) {
            sum = andSum(x, nums[i]);
            while(j <= i && sum <= k-1) {
                sum = removesum(x, nums[j++]);
            }
            if(sum > k-1) ans += i - j + 1;
        }
        j = 0;
        sum = 0;
        for(auto &i : x) for(auto &j : i) j = 0;
        for(int i = 0; i < nums.size() ; i++) {
            sum = andSum(x, nums[i]);
            while(j <= i && sum <= k) {
                sum = removesum(x, nums[j++]);
            }
            if(sum > k)ans -= i - j + 1;
        }
        cout << ans << endl;
        return ans;
    }
};
