class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        N = len(nums)
        INF = (1 << 32) - 1

        count = 0

        @cache
        def go(index, v):
            if v < k:
                return 0
            if v == 0:
                if v == k:
                    return N - index + 1
                return 0
            if index == N:
                if v == k:
                    return 1
                return 0

            total = 0
            if v == k:
                total += 1
            total += go(index + 1, v & nums[index])
            return total

        for i in range(N):
            count += go(i, INF)
            #print(i, count)

        return count
