class Solution:
    def countSubarrays(self, a: List[int], k: int) -> int:
        n=len(a)
        ans=0
        d=defaultdict(int)
        for i in range(n):
            td=defaultdict(int)
            if a[i]==k:
                ans+=1
            td[a[i]]=1
            for key in d:
                an=key&a[i]
                if an==k:
                    ans+=d[key]
                td[an]+=d[key]
            d=td
        return ans
