class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long n = nums.size();
long long cNtO = 0;
unordered_map<long long, long long> cuRRen;

for (long long i = 0; i < n; ++i)
{
    unordered_map<long long, long long> nExT;

    if (nums[i] == k)
    {
        ++cNtO;
    }
    nExT[nums[i]] = 1;

    for (auto paIR : cuRRen)
    {
        long long VaLUe = paIR.first;
        long long freQUEnc = paIR.second;
        long long anReSuLT = VaLUe & nums[i];

        if (anReSuLT == k)
        {
            cNtO += freQUEnc;
        }

        nExT[anReSuLT] += freQUEnc;
    }

    cuRRen = nExT;
}

return cNtO;
    }
};
