#define ll long long
class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();

        ll ans = 0;

        map<ll, ll> prev;

        for (int i = 0; i < n; ++i)
         {
            map<ll, ll> curr;

            ll tgt = k;

            if (nums[i] == tgt)
            {
                ++ans;
            }
            curr[nums[i]] = 1;

            for (auto it : prev) {
                ll val = it.first;
                ll freq = it.second;
                ll newAnd = val & nums[i];
                if (newAnd == tgt) {
                    ans += freq;
                }
                curr[newAnd] += freq;
            }

            prev = curr;
        }

        return ans;
    }
};
