class Solution {
public:
    long long countSubarrays(vector<int>& arr, int k) {
        int n = arr.size();
        long long count = 0;

        unordered_map<long long, long long> umap;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> tempUmap; 
            if (arr[i] == k) 
            count++;
            tempUmap[arr[i]] = 1;
            for (auto x:umap) 
            {
                long long a = x.first & arr[i];
                if(a == k) 
                count += x.second;
                tempUmap[a] += x.second;
            }
            umap = tempUmap;
        }
        return count;
    }
};
