class Solution {
    public long countSubarrays(int[] nums, int k) {
        long ans = 0;
        HashMap<Long, Long> results = new HashMap<>();

        for (int num : nums) {
            HashMap<Long, Long> curr = new HashMap<>();
            if (num == k) {
                ans++;
            }
            curr.put((long) num, 1L);

            for (Map.Entry<Long, Long> entry : results.entrySet()) {
                long nR = entry.getKey() & num;
                if (nR == k) {
                    ans += entry.getValue();
                }
                curr.put(nR, curr.getOrDefault(nR, 0L) + entry.getValue());
            }

            results = curr;
        }

        return ans;
    }
}
