class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        long long res = 0, n = nums.size(), temp, temp2;
        map<long long, long long> mp;

        for (int i = 0; i < n; ++i) {
            temp = k;
            map<long long, long long> aux;

            if (nums[i] == temp)res++;

            aux[nums[i]] = 1;

            for (auto &it: mp) {
                auto num = it.first;
                auto frq = it.second;
                temp2 = num & nums[i];
                if (temp2 == temp)  res += frq;
                aux[temp2] += frq;
            }
            mp = aux;
        }
        return res;
    }
};
