class Solution {
public:
    long long countSubarrays(vector<int>& nums, int target) {
        long long i, j, k, n = nums.size(), ans = 0, mi, ma;
        deque<int> v[32];
        for(i=0;i<n;i++){
            for(j=0;j<31;j++){
                if((nums[i] & (1 << j)) == 0)  v[j].push_back(i);
            }
        }
        for(j=0;j<31;j++)   v[j].push_back(n);

        for(i=0;i<n;i++){
            mi = n, ma = -1;
            for(j=0;j<31;j++){
                if(target & (1 << j)){
                    mi = min((int)mi, v[j].front());
                    if(v[j].front() == i)   v[j].pop_front();
                }
                else{
                    ma = max((int)ma, v[j].front());
                    if(v[j].front() == i)   v[j].pop_front();
                }
            }
            if(mi > ma)  ans += (mi - ma);
        }
        return ans;
    }
};
