class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long  count = 0;
        unordered_map<long long, long long> freq;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long , long long> map;
            for (auto& j : freq) {
                long long val = j.first & nums[i];
                map[val] += j.second;
            }
            map[nums[i]]++;
            freq = map;
            count += freq[k];
        }

        return count;
    }
};
