class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0
        subarrays = defaultdict(int)

        for i in range(n):
            new_subarrays = defaultdict(int)

            if nums[i] == k:
                count += 1

            for key in subarrays:
                new_and = key & nums[i]
                if new_and == k:
                    count += subarrays[key]
                new_subarrays[new_and] += subarrays[key]

            new_subarrays[nums[i]] += 1
            subarrays = new_subarrays

        return count
