class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        int n = a.size();
        vector<vector<long long>> e(32, vector<long long> (n));
        for(int i = 0; i < 32; i++){
            int cur = 0;
            for(int j = 0; j < n; j++){
                if((a[j] >> i) & 1){
                    cur += 1;
                }
                else cur = 0;
                e[i][j] = cur;
            }
        }
        long long ans = 0;
        for(int i = 0; i < n; i++){
            long long mx = 0, mn = i + 1;
            for(int j = 0; j < 32; j++){
                if((k >> j) & 1){
                    mn = min(mn, e[j][i]);
                }
                else mx = max(mx, e[j][i]);
            }
            if(mn >= mx) ans = ans + mn - mx;
        }
        return ans;
    }
};
