class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        unordered_map<int, int> n2c;
        long long ans = 0;
        for (auto num: nums) {
            unordered_map<int, int> n2c2;
            if (num >= k) {
                for (auto [n, cnt]: n2c) {
                    int n2 = n; 
                    n2 &= num;
                    if (n2 == k) {
                        ans += cnt;
                    }
                    if (n2 >= k) {
                        n2c2[n2] += cnt;
                    }
                }
                n2c2[num] += 1;
                if (num == k) {
                    ++ans;
                }
            }
            swap(n2c, n2c2);
        }
        return ans;
    }
};
