#include <vector>
#include <unordered_map>

using namespace std;

class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) {
        int n = nums.size();
        long long cnt = 0;

        unordered_map<long long, long long> pre;

        for (int i = 0; i < n; ++i) {
            unordered_map<long long, long long> cr;

            if (nums[i] == k) {
                ++cnt;
            }
            cr[nums[i]] = 1;

            for (auto& [val, freq] : pre) {
                long long newr = val & nums[i];
                if (newr == k) {
                    cnt += freq;
                }
                cr[newr] += freq;
            }
            pre = cr;
        }

        return cnt;
    }
};
