class Solution {
public:
    #define ll long long
    ll countSubarrays(vector<int>& nums, int k) {
        ll res = count(begin(nums),end(nums),k);
        int n = nums.size();
        bitset<64> req(k);

            map<ll,ll>m,nr;
        for (int i = 0; i < n; ++i) {
            nr.clear();
            nr[nums[i]]=1;
            for(auto [a,b]:m){
                if((a&nums[i]) == k)res+=b;
                nr[(a&nums[i])]+=b;
            }
            m=nr;
        }

        return res;
    }
};
