class Solution {
public:
   int check(vector<int>&v,int l,int r){
       int g=0;
        for(int j=0;j<v.size();j++){
            if(v[j]==r-l+1)g+=(1<<j);     
        }
        return g;
    }
    long long countSubarrays(vector<int>& nums, int k) {
        long long ans=0;
        vector<int>v(31,0);
        int i=0;
        for(int j=0;j<nums.size();j++){
             for(int k=0;k<31;k++){
                 if(nums[j]&(1<<k))v[k]++;
             }
             while(i<=j&&check(v,i,j)<k){
                for(int k=0;k<31;k++){
                 if(nums[i]&(1<<k))v[k]--;
                }
                 i++;
             }
            ans+=j-i+1;
        }
        long long ans2=0;
        k++;
        vector<int>v1(31,0);
         i=0;
        for(int j=0;j<nums.size();j++){
             for(int k=0;k<31;k++){
                 if(nums[j]&(1<<k))v1[k]++;
             }
             while(i<=j&&check(v1,i,j)<k){
                for(int k=0;k<31;k++){
                 if(nums[i]&(1<<k))v1[k]--;
                }
                 i++;
             }
            ans2+=j-i+1;
        }
        cout<<endl;
        return ans-ans2;}
};
