class Solution {
public:
    long long countSubarrays(vector<int>& nums, int k) 
    {
        unordered_map<long long, long long> PR;
        int n = nums.size();
        long long count = 0;
        for (auto i:nums) 
        {
            unordered_map<long long, long long> CR; 
            if (i == k) 
            {
                count++;
            }
            CR[i] = 1;
            for (auto& [val, freq] : PR) 
            {
                long long NAR = val & i;
                if (NAR == k) {
                    count += freq;
                }
                CR[NAR] += freq;
            }
            PR = CR;
        } 
        return count;
    }
};
