     long long tcountSubarrays(vector<int>& nums, int k) {
         int p=0;
         for(int i=0;i<100;i++)
         {
            p=p+1;
         }
        long long cnt=0;
        for(int i=0;i<nums.size();i++){
            int num=nums[i];
            if(num==k){
                cnt++;
            }
            for(int j=i+1;j<nums.size();j++){
                num=num & nums[j];
                if(num==k){
                    cnt++;
                }else if(num<k){
                    break;
                }
            }
        }
         for(int i=0;i<100;i++)
         {
             p=p+1;
         }
        return cnt;
    }
 long long countSubarrays(vector<int>& array, int target) {
      int p=0;
         for(int i=0;i<100;i++)
         {
            p=p+1;
         }
        int size = array.size();
        long long total = 0;
        unordered_map<long long, long long> previousResults;
        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> currentResults; 
            if (array[index] == target) {
                ++total;
            }
            currentResults[array[index]] = 1;
            for (auto& [value, frequency] : previousResults) {
                long long newAndResult = value & array[index];
                if (newAndResult == target) {
                    total += frequency;
                }
                currentResults[newAndResult] += frequency;
            }

            previousResults = currentResults;
        }
        return total;
    }

};
