class Solution {
public:
    long long countSubarrays(vector<int>& array, int target) {
        int size = array.size();
        long long matchCount = 0;

        unordered_map<long long, long long> previousResults;

        for (int index = 0; index < size; ++index) {
            unordered_map<long long, long long> currentResults; 

            if (array[index] == target) {
                ++matchCount;
            }
            currentResults[array[index]] = 1;

            updateResults(array[index], previousResults, currentResults, matchCount, target);

            previousResults = currentResults;
        }

        return matchCount;
    }

private:
    void updateResults(int currentValue, unordered_map<long long, long long>& previous, unordered_map<long long, long long>& current, long long& matchCount, int target) {
        for (auto& [value, frequency] : previous) {
            long long newAndResult = value & currentValue;
            if (newAndResult == target) {
                matchCount += frequency;
            }
            current[newAndResult] += frequency;
        }
    }
};
