class Solution {
    public long countSubarrays(int[] nums, int k) {
        int n = nums.length;
        HashMap<Integer,Long> map = new HashMap<>();
        long ans =0;

        for(int i=0;i<n;i++){
            HashMap<Integer,Long> temp = new HashMap<>();
            for (Map.Entry<Integer, Long> entry : map.entrySet()) {
                int curr = entry.getKey() & nums[i];
                temp.put(curr, temp.getOrDefault(curr, 0L) + entry.getValue());
            }

            temp.put(nums[i], temp.getOrDefault(nums[i], 0L) + 1);
            map = temp;
            if (map.containsKey(k)) {
                ans += map.get(k);
            }
        }

        return ans;
    }
}
