/* ------------------------------------------------------- SOLUTION TO THE PROBLEM ------------------------------------------------- */
using ll=long long ;
map<ll,ll> mpp1;
class Solution {
public:
    long long countSubarrays(vector<int>& a, int k) {
        ll ans=0 ;
        mpp1.clear();
        for(int i=0;i<a.size();i++){
            map<ll,ll> mpp2;
            mpp2[a[i]]=1;
            for(auto &x:mpp1){
                mpp2[(x.ff&a[i])]+=x.ss;
                if((x.ff&a[i])==k) ans+=x.ss;
            }
            mpp1.clear();
            for(auto &x:mpp2) mpp1[x.ff]=x.ss;
        }

        map<int,int> mpp;
        for(auto i:a) mpp[i]++;
        return ans+mpp[k];        
    }
};
