from typing import List

class Solution:
    def countSubarrays(self, nums: List[int], k: int) -> int:
        n = len(nums)
        count = 0

        prevResults = {}

        for i in range(n):
            currResults = {}

            if nums[i] == k:
                count += 1  # Inc count if currnt ele is k
            currResults[nums[i]] = 1

            for x, freq in prevResults.items():
                newAndResult = x & nums[i]
                if newAndResult == k:
                    count += freq  # Inc count by freq if result is k
                if newAndResult in currResults:
                    currResults[newAndResult] += freq
                else:
                    currResults[newAndResult] = freq

            prevResults = currResults  # Updateprevious res fr nxt itr

        return count  # Return cnt
